// 1. Remove backticks
let raw = $json.output.replace(/```json|```/g, '').trim();

// 2. Parse JSON safely
let parsed;
try {
  parsed = JSON.parse(raw);
} catch (e) {
  throw new Error("Invalid JSON inside AI output");
}

// 3. Extract the URLs
const urls = parsed.source_links || [];

return {
  urls
};

// Merge all incoming items into one combined output item

// 1. Collect all input JSONs
const allInputs = items.map(i => i.json);

// 2. Merge logic: 
// If all inputs are identical, output just one.
// If inputs vary, combine them into an array.

let output = {};

// Check if all items are identical
const allSame = allInputs.every(obj => JSON.stringify(obj) === JSON.stringify(allInputs[0]));

if (allSame) {
  // All items identical → return just one
  output = allInputs[0];
} else {
  // Not identical → return all inside an array
  output = {
    combined_inputs: allInputs
  };
}

// 3. Return a single output item
return [{ json: output }];
