// Loop over each item passed from the Merge node
for (const item of items) {
	// Get the country value (make sure to match the field name exactly)
	const countryRaw = item.json["Country "];
	let country = "";
	if (countryRaw) {
		country = countryRaw.toLowerCase().trim();
	}

	// Define arrays for the different time zone groups
	const t1Countries = ["usa", "united states", "canada"];
	const europeanCountries = [
		"albania", "andorra", "armenia", "austria", "azerbaijan", "belarus", "belgium",
		"bosnia and herzegovina", "bulgaria", "croatia", "cyprus", "czech republic", "czechia",
		"denmark", "estonia", "finland", "france", "georgia", "germany", "greece", "hungary",
		"iceland", "ireland", "italy", "kosovo", "latvia", "liechtenstein", "lithuania",
		"luxembourg", "malta", "moldova", "monaco", "montenegro", "netherlands", "north macedonia",
		"norway", "poland", "portugal", "romania", "russia", "san marino", "serbia", "slovakia",
		"slovenia", "spain", "sweden", "switzerland", "turkey", "ukraine", "united kingdom",
		"vatican city"
	];
	const t3Countries = ["australia", "new zealand"];

	// Determine and populate the Time Zone based on the country value
	if (t1Countries.includes(country)) {
		item.json["Time Zone"] = "T1";
	} else if (europeanCountries.includes(country)) {
		item.json["Time Zone"] = "T2";
	} else if (t3Countries.includes(country)) {
		item.json["Time Zone"] = "T3";
	} else {
		// For any other country not defined above, assign T4.
		// If you want to limit to T1, T2, or T3 only, you can decide to leave it blank or assign a default value.
		item.json["Time Zone"] = "T4";
	}
}

return items;

// Array of sender emails to rotate through
const senderEmails = [
  "kiaghasem.dev@gmail.com",
  "kia@kamexa.ai",
  "youremail@gmail.com"
];

// Get all input items from the Time Zone node
const items = $input.all();

// Process each item
for (let i = 0; i < items.length; i++) {
  // Calculate which email to use based on position (cycling through the array)
  const emailIndex = i % senderEmails.length;

  // Assign the sender email to the current item
  items[i].json["Sender Email"] = senderEmails[emailIndex];
}

// Return the updated items
return items;

// Loop through each item in the input
for (let i = 0; i < items.length; i++) {
  // Calculate the batch value (B1, B2, B3, cycling)
  const batchNumber = (i % 3) + 1;

  // Update the Batch field
  items[i].json.Batch = `B${batchNumber}`;
}

// Return the updated items
return items;

// Function to generate a random 6-digit number
function generateRandomToken() {
  return Math.floor(100000 + Math.random() * 900000);
}

// Process each item from the Sender Email node
for (let i = 0; i < items.length; i++) {
  // Add the token parameter to each item
  items[i].json.token = generateRandomToken();
}

// Return the modified items
return items;
