SELECT COALESCE(
    json_agg(t), '[]'::json
) AS announcements
FROM (
    SELECT *
    FROM public.newsletter_announcement
    WHERE created_at > NOW() - INTERVAL '1 month'
) t;

SELECT COALESCE(
    json_agg(t), '[]'::json
) AS achievements
FROM (
    SELECT *
    FROM public.achievements
    WHERE created_at >= NOW() - INTERVAL '1 month'
) t;

SELECT COALESCE(
    json_agg(t), '[]'::json
) AS events
FROM (
    SELECT *
    FROM public.events
    WHERE start_date > NOW()
) t;

