from datetime import datetime

# Results array
results = []

# Process all employee data
for item in _input.all():
    employee = item.json

    # Get publication_date from the current employee record
    publication_date_str = employee.get('publication_date')

    if not publication_date_str:
        continue  # Skip if no publication_date found

    # Parse the publication date
    try:
        publication_date = datetime.fromisoformat(publication_date_str.replace('Z', '+00:00'))
        publication_month_day = publication_date.strftime('%m-%d')
    except (ValueError, AttributeError):
        continue  # Skip if invalid publication_date format

    # Check birthday
    if employee.get('date_of_birth'):
        try:
            date_of_birth = datetime.fromisoformat(employee['date_of_birth'].replace('Z', '+00:00'))
            date_of_birth_month_day = date_of_birth.strftime('%m-%d')

            if date_of_birth_month_day == publication_month_day:
                # Calculate age as of publication date
                age = publication_date.year - date_of_birth.year
                # Adjust if birthday hasn't occurred yet this year
                if (publication_date.month, publication_date.day) < (date_of_birth.month, date_of_birth.day):
                    age -= 1
                employee['age'] = age

                results.append({
                    'json': {
                        'birthday': employee,
                    }
                })
        except (ValueError, AttributeError):
            # Skip invalid birthday dates
            continue

    # Check work anniversary
    if employee.get('join_date'):
        try:
            join_date = datetime.fromisoformat(employee['join_date'].replace('Z', '+00:00'))
            join_month_day = join_date.strftime('%m-%d')

            if join_month_day == publication_month_day:
                # Calculate years of service as of publication date
                years_of_service = publication_date.year - join_date.year
                # Adjust if anniversary hasn't occurred yet this year
                if (publication_date.month, publication_date.day) < (join_date.month, join_date.day):
                    years_of_service -= 1

                employee['years_of_service'] = years_of_service

                results.append({
                    'json': {
                        'anniversary': employee
                    }
                })
        except (ValueError, AttributeError):
            # Skip invalid join dates
            continue

# Always return results - n8n needs at least one item
if results:
    return results
else:
    return [{'json': {'no_matches': True}}]
