// This Code node processes all incoming items
return items.map(item => {
  const json = item.json || {};
  const binary = item.binary || {};

  // Check if any binary data (attachments) exist
  const hasBinaryAttachment = binary && Object.keys(binary).length > 0;

  // Additional checks for JSON-only attachment indicators
  const hasJsonAttachment =
    json.attachments?.length > 0 ||
    json.attachment ||
    json.hasAttachment ||
    json.headers?.["content-type"]?.includes("multipart") ||
    false;

  const hasAttachment = hasBinaryAttachment || hasJsonAttachment;

  return {
    json: {
      ...json,
      doc_present: hasAttachment ? "true" : "false",
    },
    // Keep the binary data untouched
    binary,
  };
});

// Get input items (JSON + binary)
const items = $input.all();

// Check empty conditions
const isEmpty =
    !items ||
    items.length === 0 ||
    (
        items.length === 1 &&
        Object.keys(items[0].json || {}).length === 0 &&
        (!items[0].binary || Object.keys(items[0].binary).length === 0)
    );

// If empty ‚Üí return only is_empty flag
if (isEmpty) {
    return [
        {
            json: {
                is_empty: true
            }
        }
    ];
}

// If NOT empty ‚Üí unify the entire JSON into one field
return items.map(item => {
    // Flattened string of full JSON
    const unifiedOutput = JSON.stringify(item.json, null, 2);

    return {
        json: {
            is_empty: false,
            competency_analysis: unifiedOutput
        },
        binary: item.binary || undefined
    };
});

