# Extract leads from Apollo API response
output = []

for item in _input.all():
    data = item.json

    # Handle if data is a list (wrapped in array)
    if isinstance(data, list) and len(data) > 0:
        data = data[0]

    # Extract people array
    people = data.get('people', [])

    # Create a separate item for each person/lead
    for person in people:
        output.append({
            'json': person
        })

# Return output, or return input if no people found
if output:
    return output
else:
    return _input.all()
