const results = $json.results || [];
if (!results.length) {
  return [];
}

const result = results[0]; // First result
const allLinks = result.links?.internal || [];
const searchUrl = result.url || "";
const sourceDomain = searchUrl.split("/")[2] || "unknown";

const isArticleLink = (url) => {
  return (
    typeof url === 'string' &&
    url.includes(sourceDomain) &&
    (
      url.includes('/chemical/') ||
      url.includes('/general/') ||
      url.includes('/hydrogen/') ||
      url.includes('/petro') ||
      url.includes('/opinion/') ||
      url.includes('/interviews/') ||
      url.includes('/people/') ||
      url.includes('/fertilizer/') ||
      url.includes('/gas/') ||
      url.includes('/digitization/') ||
      url.includes('/start-ups/') ||
      url.includes('/report/')
    ) &&
    /\d{5,6}$/.test(url)
  );
};

const filtered = allLinks.filter(link =>
  isArticleLink(link.href)
);

return filtered.map(link => ({
  json: {
    article_url: link.href,
    article_title: link.text || null,
    source: sourceDomain
  }
}));

